mapper.update:
update sf_study
set
auth_userid = #{auth_userid}
,study_title = #{study_title}
,loc_no =
#{loc_no}
,field_no = #{field_no}
,study_content = #{study_content}
,due_time = #{due_time}
,penalty_min = #{penalty_min}
,penalty_money =
#{penalty_money}
,limit_time = #{limit_time}
where
study_no = #{study_no}
